import base64
import json


def decrypt_base64_json(encoded_data):
    # 解码 Base64 编码的数据
    decoded_data = base64.b64decode(encoded_data)
    # 将解码后的数据转换为字符串
    decoded_string = decoded_data.decode('utf-8')
    # 解析 JSON 数据
    decoded_json_data = json.loads(decoded_string)

    return decoded_json_data


